<?php
namespace app\library\exception;
use think\exception\Handle;
use think\Response;

class Http extends Handle{

    public function render(\Exception $e)
    {
        $result = [
            'code' => 10000,
            'mark' => 10000,
            'msg'  => '服务器繁忙，请稍后再试',
            'time' => $_SERVER['REQUEST_TIME'],
            'data' => '',
        ];
        return json($result);
        //return parent::render($e); // TODO: Change the autogenerated stub
    }
}